mapper.insert:
SELECT seq_tgt_dismant_id.NEXTVAL from DUAL
insert into UO_TGT_DISMANT (ID, PERIOD_TYPE, PERIOD_DATE, ACTUAL_VAL,COMPUTE_DT,TGT_ID,TGT_VAL,TGT_PERCENT,TGT_WEIGHT_IDX) SELECT seq_tgt_dismant_id.NEXTVAL, m.* FROM( SELECT #{item.periodType}, #{item.periodDate}, #{item.actualVal}, #{item.computeDt}, #{item.tgtId}, #{item.tgtVal}, #{item.tgtPercent}, #{item.tgtWeightIdx} FROM DUAL
) m

update.foreach:
  update UO_TGT_DISMANT set
   ACTUAL_VAL=#{item.actualVal},
   COMPUTE_DT=#{item.computeDt,jdbcType=DATE},
   ACTUAL_VAL_LAST=#{item.actualValLast}
where
   TGT_ID=#{item.tgtId}
and PERIOD_DATE=#{item.periodDate}

update UO_TGT_DISMANT set
GROWTH_RATE=#{item.growthRate}
where ID=#{item.id}
