mapper.select:
select count(1) from UO_OP_ACTIVITY_DETAIL where
    HEAD_ID = #{headId} and PLAN_DT = #{planDtWid}

    and GROUP_ID = #{groupId}

select * from ( select t.*, ROWNUM rn from (select UO_OP_ACTIVITY_DETAIL.IS_PUSH, to_char(UO_OP_ACTIVITY_DETAIL.PUSH_DATE, 'yyyy-mm-dd hh24:mi') PUSH_DATE_STR, UO_OP_ACTIVITY_DETAIL.PUSH_STATUS, UO_OP_ACTIVITY_DETAIL.EPB_PRODUCT_NAME, UO_OP_ACTIVITY_DETAIL.EPB_PRODUCT_ID, UO_OP_ACTIVITY_DETAIL.USER_ID, UO_OP_ACTIVITY_DETAIL.GROUP_ID, UO_OP_ACTIVITY_DETAIL.SMS_CONTENT, UO_OP_ACTIVITY_PLAN.PLAN_TYPE, UO_OP_ACTIVITY_DETAIL.HEAD_ID, UO_OP_ACTIVITY_DETAIL.ACTIVITY_DETAIL_ID, UO_OP_ACTIVITY_DETAIL.REC_PIECE_PRICE, UO_OP_ACTIVITY_PLAN.ACTIVITY_STAGE from UO_OP_ACTIVITY_DETAIL,UO_OP_ACTIVITY_PLAN where UO_OP_ACTIVITY_DETAIL.HEAD_ID=UO_OP_ACTIVITY_PLAN.HEAD_ID AND UO_OP_ACTIVITY_DETAIL.PLAN_DT=UO_OP_ACTIVITY_PLAN.PLAN_DATE_WID AND UO_OP_ACTIVITY_DETAIL.HEAD_ID = #{headId} and UO_OP_ACTIVITY_DETAIL.PLAN_DT = #{planDtWid}
and UO_OP_ACTIVITY_DETAIL.GROUP_ID = #{groupId}
ORDER BY UO_OP_ACTIVITY_DETAIL.GROUP_ID ASC,UO_OP_ACTIVITY_DETAIL.ACTIVITY_DETAIL_ID ASC) t where ROWNUM <= #{end} ) where RN >= #{start}

mapper.insert:
insert into UO_OP_ACTIVITY_CONTENT_TMP(activity_detail_id,head_id,sms_content) SELECT A.* FROM(
SELECT #{item.activityDetailId} activityDetailId, #{item.headId} headId, #{item.smsContent} smsContent FROM dual
)A
