mapper.select:
select * from (
    select t.*, ROWNUM rn from (
        select IS_PUSH, to_char(PUSH_DATE, 'yyyy-mm-dd hh24:mi') PUSH_DATE_STR, PUSH_STATUS, EPB_PRODUCT_NAME, USER_VALUE, EPB_PRODUCT_ID, USER_ID, GROUP_ID, IN_GROWTH_PATH, PATH_ACTIVE, SMS_CONTENT from UO_OP_ACTIVITY_DETAIL where HEAD_ID = #{headId} and PLAN_DT = #{planDtWid}
    ) t where ROWNUM <= #{end}
) where RN >= #{start}

mapper.update:
--对计划日期在最近5天，且存在状态为 执行中 执行完 停止 的 推送明细的 推送状态，推送日期进行更新
UPDATE uo_op_activity_detail T2 SET (T2.PUSH_STATUS,T2.PUSH_DATE)=(
                                     SELECT P.PUSH_STATUS,P.PUSH_DATE FROM uo_op_push_list_large P WHERE P.IS_PUSH='1' AND P.SOURCE_CODE='ACTIVITY' AND P.SOURCE_ID=T2.ACTIVITY_DETAIL_ID
    ) WHERE T2.PLAN_DT>=TO_NUMBER(TO_CHAR(SYSDATE-5,'yyyymmdd')) AND EXISTS (
    SELECT AP.HEAD_ID FROM UO_OP_ACTIVITY_PLAN AP WHERE AP.PLAN_STATUS NOT IN ('0','1') AND AP.HEAD_ID=T2.HEAD_ID
    )
