mapper.select:
select * from (
  select t.*, ROWNUM rn from (
    select HEAD_ID, ACTIVITY_NAME, ACTIVITY_STAGE, PREHEAT_STATUS, to_char(PREHEAT_START_DT, 'yyyymmdd') PREHEAT_START_DT, to_char(PREHEAT_END_DT, 'yyyymmdd') PREHEAT_END_DT, FORMAL_STATUS, to_char(FORMAL_START_DT, 'yyyymmdd') FORMAL_START_DT, to_char(FORMAL_END_DT, 'yyyymmdd') FORMAL_END_DT
    from UO_OP_ACTIVITY_HEADER where 1=1

        and ACTIVITY_NAME = #{name}

    order by HEAD_ID desc
  ) t where ROWNUM <=#{end}
) where RN >= #{start}

mapper.update:
update UO_OP_ACTIVITY_HEADER set START_DATE = to_date(#{startDt}, 'yyyy-mm-dd'), END_DT = to_date(#{endDt}, 'yyyy-mm-dd'), ACTIVITY_TYPE = #{actType}

    , DATE_RANGE = #{dateRange}

where HEAD_ID = #{headId}
