mapper.select:
select * from (
  select t.*, ROWNUM rn from (
    select HEAD_ID, ACTIVITY_NAME, ACTIVITY_TYPE, to_char(START_DATE, 'yyyymmdd') START_DATE, to_char(END_DATE, 'yyyymmdd') END_DATE, COVER_NUM, CONVERT_NUM, NUM_YEAR_PERCENT, CONVERT_AMOUNT, AMOUNT_YEAR_PERCENT,STATUS, to_char(BEFORE_DATE, 'yyyymmdd') BEFORE_DATE, to_char(AFTER_DATE, 'yyyymmdd') AFTER_DATE
    from UO_OP_ACTIVITY_HEADER where 1=1

        and ACTIVITY_NAME = #{name}

    order by HEAD_ID desc
  ) t where ROWNUM <=#{end}
) where RN >= #{start}

mapper.update:
update UO_OP_ACTIVITY_HEADER set START_DATE = to_date(#{startDt}, 'yyyy-mm-dd'), END_DT = to_date(#{endDt}, 'yyyy-mm-dd'), ACTIVITY_TYPE = #{actType}

    , DATE_RANGE = #{dateRange}

where HEAD_ID = #{headId}
