mapper.select:
select * from (
select t.*, ROWNUM rn from (
  select ID, HEAD_ID, EPB_PRODUCT_ID, PRODUCT_NAME, MIN_PRICE, FORMAL_PRICE, PRODUCT_URL,NOTIFY_MIN_PRICE,CHECK_FLAG,
    CHECK_COMMENTS, ALIKE_PROD_ID, GROUP_ID from UO_OP_ACTIVITY_PRODUCT
  where HEAD_ID = #{headId}

    and EPB_PRODUCT_ID = #{productId}


    and product_name like concat('%', concat(#{productName}, '%'))


    and group_id = #{groupId}

order by insert_dt desc
) t where ROWNUM <=#{end}
) where RN >= #{start}

select count(1) from UO_OP_ACTIVITY_PRODUCT where HEAD_ID = #{headId}

    and EPB_PRODUCT_ID = #{productId}


    and product_name like concat('%', concat(#{productName}, '%'))


    and group_id = #{groupId}

mapper.update:
update UO_OP_ACTIVITY_PRODUCT
set
EPB_PRODUCT_ID = #{productId}, SKU_CODE = #{skuCode},
PRODUCT_NAME = #{productName}, MIN_PRICE = #{minPrice}, FORMAL_PRICE = #{formalPrice}, PRODUCT_ATTR = #{productAttr}
where ID = #{id}

mapper.insert:
insert into UO_OP_ACTIVITY_PRODUCT(ID,HEAD_ID, EPB_PRODUCT_ID, PRODUCT_NAME, MIN_PRICE, FORMAL_PRICE, PRODUCT_URL, NOTIFY_MIN_PRICE, GROUP_ID) select SEQ_ACTIVITY_PRODUCT_ID.nextval,A.* FROM (
SELECT #{item.headId}, #{item.productId}, #{item.productName}, #{item.minPrice}, #{item.formalPrice}, #{item.activityIntensity}, #{item.productAttr}, #{item.activityStage}, #{item.productUrl}, #{item.skuCode}, #{item.notifyMinPrice}, #{item.groupId} FROM DUAL
) A

mapper.select:
select count(1) from UO_OP_ACTIVITY_PRODUCT
where head_id = #{headId} and activity_stage = #{stage} and EPB_PRODUCT_ID in

    #{item}
