mapper.select:
SELECT * FROM(
SELECT A.*,ROWNUM RN FROM
(select coupon_id, coupon_denom, coupon_threshold, coupon_name, coupon_url, coupon_desc, coupon_type, coupon_num, to_char(valid_end, 'yyyy-mm-dd') valid_end, coupon_info1, coupon_info2, coupon_display_name, valid_status, coupon_source, user_value, life_cycle, path_active, discount_level, check_flag, check_comments from UO_OP_COUPON

    where VALID_STATUS = #{validStatus}

order by coupon_threshold asc, coupon_denom desc
) A WHERE ROWNUM <=#{endRow}
) WHERE RN >=#{startRow}

mapper.update:
update UO_OP_COUPON
set COUPON_DENOM = #{couponDenom},COUPON_THRESHOLD = #{couponThreshold},COUPON_URL = #{couponUrl},COUPON_DESC = #{couponDesc},COUPON_TYPE = #{couponType},COUPON_NUM = #{couponNum},COUPON_INFO1 = #{couponInfo1},COUPON_INFO2 = #{couponInfo2},COUPON_DISPLAY_NAME = #{couponDisplayName},
VALID_STATUS = #{validStatus}, VALID_END = to_date(#{validEnd}, 'yyyy-mm-dd'), USER_VALUE = #{userValue}, LIFE_CYCLE = #{lifeCycle}, path_active = #{pathActive}
where COUPON_ID = #{couponId}

mapper.select:
select distinct COUPON_ID from UO_OP_DAILY_GROUP_COUPON

    COUPON_ID in

        #{item}

mapper.delete:
delete from UO_OP_COUPON where 1=1

    and COUPON_ID in

        #{item}
