mapper.select:
select * from (
select t.*, ROWNUM rn from (
SELECT T1.HEAD_ID, T1.TOTAL_NUM, T1.OPT_NUM, T1.TOUCH_DT, T1.STATUS,
T2.TOUCH_RATE, T2.CONVERT_COUNT, T2.CONVERT_RATE, T2.CONVERT_AMOUNT
FROM UO_OP_DAILY_HEADER T1
LEFT JOIN UO_OP_DAILY_STATIS T2 ON T1.HEAD_ID = T2.HEAD_ID
ORDER BY TOUCH_DT desc
) t where ROWNUM <= #{end}
)where rn >= #{start}

select count(1) from UO_OP_DAILY_HEADER
where 1=1

    and to_char(touch_dt, 'YYYY-MM-DD') = #{touchDt}

select * from (
select t.*, ROWNUM rn from (
SELECT HEAD_ID, TOUCH_DT, ACTUAL_NUM, PLAN_NUM, FAILD_NUM, SUCCESS_NUM, STATUS
FROM UO_OP_DAILY_HEADER
where 1=1 and STATUS in ('doing', 'done', 'finished')

    and to_char(TOUCH_DT, 'YYYY-MM-DD') = #{touchDt}

ORDER BY TOUCH_DT desc
) t where ROWNUM <= #{end}
)where rn >= #{start}

select count(1) from UO_OP_DAILY_HEADER
where 1=1 and STATUS in ('doing', 'done', 'finished')

    and to_char(touch_dt, 'YYYY-MM-DD') = #{touchDt}
