mapper.insert:
insert into UO_OP_ACTIVITY_PLAN(PLAN_ID,HEAD_ID, USER_CNT, PLAN_DATE, PLAN_STATUS, ACTIVITY_STAGE,PLAN_DATE_WID,PLAN_TYPE,SUCCESS_NUM) select UO_OP_ACTIVITY_PLAN_SEQ.NEXTVAL,p.* from(
select #{item.headId}, #{item.userCnt}, #{item.planDate}, #{item.planStatus}, #{item.stage}, #{item.planDateWid}, #{item.planType}, #{item.successNum} from dual
) p

mapper.select:
select *
from (
         select tt.*, ROWNUM RN
         from (
                  SELECT SMS_CONTENT, CNT, LENGTH(SMS_CONTENT) SMSLENGTH
                  FROM (select T.SMS_CONTENT, COUNT(*) CNT
                        from UO_OP_ACTIVITY_DETAIL T,UO_OP_ACTIVITY_PLAN_GROUP G
                        WHERE T.PLAN_ID=G.PLAN_ID AND T.GROUP_ID=G.GROUP_ID AND G.CHECK_FLAG='Y' AND T.PLAN_ID=#{planId}
                        GROUP BY T.SMS_CONTENT)
                  ORDER BY CNT DESC,SMS_CONTENT ASC
              ) tt where ROWNUM <= #{end}
     )  where RN >= #{start}
