mapper.insert:
insert into UO_OP_ACTIVITY_TEMPLATE(
    TMP_CONTENT, INSERT_DT, INSERT_BY, IS_PERSONAL, TMP_NAME, IS_PROD_NAME, IS_PROD_URL, IS_PRICE, IS_PROFIT
) values (#{content}, #{insertDt}, #{insertBy}, #{isPersonal}, #{name}, #{isProdName}, #{isProdUrl}, #{isPrice}, #{isProfit})

mapper.update:
