mapper.select:
SELECT SUM(W_ORDERS.REAL_FEE) TOTAL_GMV FROM W_ORDERS JOIN W_DATE ON W_ORDERS.ORDER_DT_WID = W_DATE.ROW_WID
${joinInfo}
WHERE W_ORDERS.VALID_STATUS = 1
${whereInfo}

SELECT SUM(W_ORDERS.REAL_FEE) kpiVal, ${period_name} kpiDate FROM W_ORDERS JOIN W_DATE ON W_ORDERS.ORDER_DT_WID = W_DATE.ROW_WID
${joinInfo}
WHERE W_ORDERS.VALID_STATUS = 1
${whereInfo}
GROUP BY ${period_name} ORDER BY ${period_name} ASC

SELECT ${period_name} kpiDate, SUM( W_ORDERS.REAL_FEE ) kpiVal, SUM(CASE WHEN W_ORDERS.IS_FP='Y' THEN W_ORDERS.REAL_FEE ELSE 0 END) fpKpiVal, SUM(CASE WHEN W_ORDERS.IS_FP='N' THEN W_ORDERS.REAL_FEE ELSE 0 END) spKpiVal FROM W_ORDERS JOIN W_DATE ON W_ORDERS.ORDER_DT_WID = W_DATE.ROW_WID
${joinInfo}
WHERE W_ORDERS.VALID_STATUS = 1
${whereInfo}
GROUP BY ${period_name} ORDER BY ${period_name} ASC

SELECT SUM(W_ORDERS.REAL_FEE) kpiVal, SUM(CASE WHEN W_ORDERS.IS_FP='Y' THEN W_ORDERS.REAL_FEE ELSE 0 END) fpKpiVal, SUM(CASE WHEN W_ORDERS.IS_FP='N' THEN W_ORDERS.REAL_FEE ELSE 0 END) spKpiVal FROM W_ORDERS JOIN W_DATE ON W_ORDERS.ORDER_DT_WID = W_DATE.ROW_WID
${joinInfo}
WHERE W_ORDERS.VALID_STATUS = 1
${whereInfo}
