mapper.select:
select HEAD_ID, ACTIVITY_NAME, HAS_PREHEAT, PREHEAT_STATUS, to_char(PREHEAT_START_DT, 'yyyymmdd') PREHEAT_START_DT, to_char(PREHEAT_END_DT, 'yyyymmdd') PREHEAT_END_DT, FORMAL_STATUS, to_char(FORMAL_START_DT, 'yyyymmdd') FORMAL_START_DT, to_char(FORMAL_END_DT, 'yyyymmdd') FORMAL_END_DT, to_char(PREHEAT_NOTIFY_DT, 'yyyymmdd') PREHEAT_NOTIFY_DT, to_char(FORMAL_NOTIFY_DT, 'yyyymmdd') FORMAL_NOTIFY_DT, FORMAL_NOTIFY_STATUS, PREHEAT_NOTIFY_STATUS,EFFECT_FLAG from UO_QYWX_ACTIVITY_HEADER where 1=1
and ACTIVITY_NAME = #{name}
and ((date_trunc('day', PREHEAT_START_DT) <= date_trunc('day', to_date(#{date}, 'yyyy-mm-dd')) and date_trunc('day', PREHEAT_END_DT) >= date_trunc('day', to_date(#{date}, 'yyyy-mm-dd'))) or (date_trunc('day', FORMAL_START_DT) <= date_trunc('day', to_date(#{date}, 'yyyy-mm-dd')) and date_trunc('day', FORMAL_END_DT) >= date_trunc('day', to_date(#{date}, 'yyyy-mm-dd'))))
and (PREHEAT_STATUS = #{status} or FORMAL_STATUS = #{status})
order by HEAD_ID desc limit #{limit} offset #{offset}
