mapper.select:
SELECT HEAD_ID,to_char(TASK_DATE,'YYYYMMDD') TASK_DATE_STR,TASK_DATE, TOTAL_NUM, SUCCESS_NUM,CONVERT_NUM, CONVERT_RATE, CONVERT_AMOUNT, STATUS,EFFECT_DAYS,QYWX_MESSAGE_COUNT, staff_cnt, execute_staff_cnt, execute_rate FROM uo_qywx_daily_header t
TASK_DATE::date = to_date(#{taskDate},'YYYY-MM-DD')
ORDER BY HEAD_ID desc limit #{limit} offset #{offset}

mapper.update:
update uo_qywx_daily_header
set status = #{status},
    EFFECT_DAYS=#{effectDays},
    version=version + 1
where head_id = #{headId}
  and version = #{version}
