mapper.select:
SELECT (CASE WHEN COUNT(DISTINCT W_ORDER_DETAILS.USER_ID)=0 THEN 0 ELSE TRUNC(SUM(W_ORDER_DETAILS.PRICE*W_ORDER_DETAILS.QUANTITY-W_ORDER_DETAILS.DISCOUNT)/COUNT(DISTINCT W_ORDER_DETAILS.USER_ID),2) END) kpiVal FROM W_ORDER_DETAILS JOIN W_DATE ON W_ORDER_DETAILS.ORDER_DT_WID = W_DATE.ROW_WID
${joinInfo}
WHERE W_ORDER_DETAILS.VALID_STATUS = 1
${whereInfo}

SELECT (CASE WHEN COUNT(DISTINCT W_ORDER_DETAILS.USER_ID)=0 THEN 0 ELSE TRUNC(SUM(W_ORDER_DETAILS.PRICE*W_ORDER_DETAILS.QUANTITY-W_ORDER_DETAILS.DISCOUNT)/COUNT(DISTINCT W_ORDER_DETAILS.USER_ID),2) END) kpiVal, ${period_name} kpiDate FROM W_ORDER_DETAILS JOIN W_DATE ON W_ORDER_DETAILS.ORDER_DT_WID = W_DATE.ROW_WID
${joinInfo}
WHERE W_ORDER_DETAILS.VALID_STATUS = 1
${whereInfo}
GROUP BY ${period_name} ORDER BY ${period_name} ASC

WITH G1 AS ( SELECT ${period_name} PERIOD_NAME, W_ORDER_DETAILS.PRICE*W_ORDER_DETAILS.QUANTITY-W_ORDER_DETAILS.DISCOUNT ORDER_FEE, W_ORDER_DETAILS.USER_ID, W_ORDER_DETAILS.IS_FP FROM W_ORDER_DETAILS JOIN W_DATE ON W_ORDER_DETAILS.ORDER_DT_WID = W_DATE.ROW_WID
${joinInfo}
WHERE W_ORDER_DETAILS.VALID_STATUS = 1
${whereInfo}
), G2 AS ( SELECT SUM(G1.ORDER_FEE) ALL_FEE, COUNT( DISTINCT G1.USER_ID) ALL_CNT, SUM(CASE WHEN G1.IS_FP='Y' THEN ORDER_FEE ELSE 0 END) FP_FEE, COUNT(DISTINCT CASE WHEN G1.IS_FP='Y' THEN G1.USER_ID END) FP_CNT, SUM(CASE WHEN G1.IS_FP='N' THEN ORDER_FEE ELSE 0 END) RP_FEE, COUNT( DISTINCT CASE WHEN G1.IS_FP='N' THEN G1.USER_ID END) RP_CNT FROM G1 ) SELECT (CASE WHEN ALL_CNT=0 THEN 0 ELSE TRUNC(ALL_FEE/ALL_CNT,2) END) kpiVal, (CASE WHEN FP_CNT=0 THEN 0 ELSE TRUNC(FP_FEE/FP_CNT,2) END) fpKpiVal, (CASE WHEN RP_CNT=0 THEN 0 ELSE TRUNC(RP_FEE/RP_CNT,2) END) spKpiVal FROM G2

WITH G1 AS ( SELECT ${period_name} PERIOD_NAME, W_ORDER_DETAILS.PRICE*W_ORDER_DETAILS.QUANTITY-W_ORDER_DETAILS.DISCOUNT ORDER_FEE, W_ORDER_DETAILS.USER_ID, W_ORDER_DETAILS.IS_FP FROM W_ORDER_DETAILS JOIN W_DATE ON W_ORDER_DETAILS.ORDER_DT_WID = W_DATE.ROW_WID
${joinInfo}
WHERE W_ORDER_DETAILS.VALID_STATUS = 1
${whereInfo}
), G2 AS ( SELECT G1.PERIOD_NAME, SUM(G1.ORDER_FEE) ALL_FEE, COUNT( DISTINCT G1.USER_ID) ALL_CNT, SUM(CASE WHEN G1.IS_FP='Y' THEN ORDER_FEE ELSE 0 END) FP_FEE, COUNT(DISTINCT CASE WHEN G1.IS_FP='Y' THEN G1.USER_ID END) FP_CNT, SUM(CASE WHEN G1.IS_FP='N' THEN ORDER_FEE ELSE 0 END) RP_FEE, COUNT( DISTINCT CASE WHEN G1.IS_FP='N' THEN G1.USER_ID END) RP_CNT FROM G1 GROUP BY G1.PERIOD_NAME ) SELECT PERIOD_NAME kpiDate, (CASE WHEN ALL_CNT=0 THEN 0 ELSE TRUNC(ALL_FEE/ALL_CNT,2) END) kpiVal, (CASE WHEN FP_CNT=0 THEN 0 ELSE TRUNC(FP_FEE/FP_CNT,2) END) fpKpiVal, (CASE WHEN RP_CNT=0 THEN 0 ELSE TRUNC(RP_FEE/RP_CNT,2) END) spKpiVal FROM G2 ORDER BY PERIOD_NAME ASC
