mapper.select:
SELECT COUNT(*)
FROM UO_REASON_CONCERN
WHERE REASON_ID = #{reasonId}
  AND TEMPLATE_CODE = #{templateCode}
  AND REASON_KPI_CODE = #{reasonKpiCode}

mapper.delete:
DELETE FROM UO_REASON_CONCERN  WHERE REASON_ID = #{reasonId}
                                 AND TEMPLATE_CODE = #{templateCode}
                                 AND REASON_KPI_CODE = #{reasonKpiCode}
