mapper.select:
SELECT (CASE WHEN SUM(W_ORDER_DETAILS.QUANTITY)=0 THEN 0 ELSE TRUNC(SUM(W_ORDER_DETAILS.PRICE*W_ORDER_DETAILS.QUANTITY-W_ORDER_DETAILS.DISCOUNT)/SUM(W_ORDER_DETAILS.QUANTITY),2) END) SPRICE FROM W_ORDER_DETAILS JOIN W_DATE ON W_ORDER_DETAILS.ORDER_DT_WID = W_DATE.ROW_WID
${joinInfo}
WHERE W_ORDER_DETAILS.VALID_STATUS = 1
${whereInfo}

SELECT (CASE WHEN SUM(W_ORDER_DETAILS.QUANTITY)=0 THEN 0 ELSE TRUNC(SUM(W_ORDER_DETAILS.PRICE*W_ORDER_DETAILS.QUANTITY-W_ORDER_DETAILS.DISCOUNT)/SUM(W_ORDER_DETAILS.QUANTITY),2) END) kpiVal, ${period_name} kpiDate FROM W_ORDER_DETAILS JOIN W_DATE ON W_ORDER_DETAILS.ORDER_DT_WID = W_DATE.ROW_WID
${joinInfo}
WHERE W_ORDER_DETAILS.VALID_STATUS = 1
${whereInfo}
GROUP BY ${period_name} ORDER BY ${period_name} ASC

WITH G1 AS ( SELECT ${period_name} PERIOD_NAME, W_ORDER_DETAILS.PRICE*W_ORDER_DETAILS.QUANTITY-W_ORDER_DETAILS.DISCOUNT ORDER_FEE, W_ORDER_DETAILS.QUANTITY, W_ORDER_DETAILS.IS_FP FROM W_ORDER_DETAILS JOIN W_DATE ON W_ORDER_DETAILS.ORDER_DT_WID = W_DATE.ROW_WID
${joinInfo}
WHERE W_ORDER_DETAILS.VALID_STATUS = 1
${whereInfo}
), G2 AS ( SELECT SUM(G1.ORDER_FEE) ALL_FEE, SUM(G1.QUANTITY) ALL_QUANTITY, SUM(CASE WHEN G1.IS_FP='Y' THEN ORDER_FEE END) FP_FEE, SUM(CASE WHEN G1.IS_FP='Y' THEN G1.QUANTITY END) FP_QUANTITY, SUM(CASE WHEN G1.IS_FP='N' THEN ORDER_FEE END) RP_FEE, SUM(CASE WHEN G1.IS_FP='N' THEN G1.QUANTITY END) RP_QUANTITY FROM G1 ) SELECT (CASE WHEN ALL_FEE=0 THEN 0 ELSE TRUNC(ALL_FEE/ALL_QUANTITY,2) END) kpiVal, (CASE WHEN FP_FEE=0 THEN 0 ELSE TRUNC(FP_FEE/FP_QUANTITY,2) END) fpKpiVal, (CASE WHEN RP_FEE=0 THEN 0 ELSE TRUNC(RP_FEE/RP_QUANTITY,2) END) spKpiVal FROM G2

WITH G1 AS ( SELECT ${period_name} PERIOD_NAME, W_ORDER_DETAILS.PRICE*W_ORDER_DETAILS.QUANTITY-W_ORDER_DETAILS.DISCOUNT ORDER_FEE, W_ORDER_DETAILS.QUANTITY, W_ORDER_DETAILS.IS_FP FROM W_ORDER_DETAILS JOIN W_DATE ON W_ORDER_DETAILS.ORDER_DT_WID = W_DATE.ROW_WID
${joinInfo}
WHERE W_ORDER_DETAILS.VALID_STATUS = 1
${whereInfo}
), G2 AS ( SELECT G1.PERIOD_NAME, SUM(G1.ORDER_FEE) ALL_FEE, SUM(G1.QUANTITY) ALL_QUANTITY, SUM(CASE WHEN G1.IS_FP='Y' THEN ORDER_FEE END) FP_FEE, SUM(CASE WHEN G1.IS_FP='Y' THEN G1.QUANTITY END) FP_QUANTITY, SUM(CASE WHEN G1.IS_FP='N' THEN ORDER_FEE END) RP_FEE, SUM(CASE WHEN G1.IS_FP='N' THEN G1.QUANTITY END) RP_QUANTITY FROM G1 GROUP BY G1.PERIOD_NAME ) SELECT PERIOD_NAME, (CASE WHEN ALL_FEE=0 THEN 0 ELSE TRUNC(ALL_FEE/ALL_QUANTITY,2) END) kpiVal, (CASE WHEN FP_FEE=0 THEN 0 ELSE TRUNC(FP_FEE/FP_QUANTITY,2) END) fpKpiVal, (CASE WHEN RP_FEE=0 THEN 0 ELSE TRUNC(RP_FEE/RP_QUANTITY,2) END) spKpiVal FROM G2 ORDER BY PERIOD_NAME ASC
