mapper.select:
SELECT (CASE WHEN COUNT(DISTINCT W_ORDER_DETAILS.ORDER_ID)=0 THEN 0 ELSE TRUNC(SUM(W_ORDER_DETAILS.QUANTITY)/COUNT( DISTINCT W_ORDER_DETAILS.ORDER_ID),2) END) JOINRATE FROM W_ORDER_DETAILS JOIN W_DATE ON W_ORDER_DETAILS.ORDER_DT_WID = W_DATE.ROW_WID
${joinInfo}
WHERE W_ORDER_DETAILS.VALID_STATUS = 1
${whereInfo}

SELECT (CASE WHEN COUNT(DISTINCT W_ORDER_DETAILS.ORDER_ID)=0 THEN 0 ELSE TRUNC(SUM(W_ORDER_DETAILS.QUANTITY)/COUNT(DISTINCT W_ORDER_DETAILS.ORDER_ID),2) END) kpiVal, ${period_name} kpiDate FROM W_ORDER_DETAILS JOIN W_DATE ON W_ORDER_DETAILS.ORDER_DT_WID = W_DATE.ROW_WID
${joinInfo}
WHERE W_ORDER_DETAILS.VALID_STATUS = 1
${whereInfo}
GROUP BY ${period_name} ORDER BY ${period_name} ASC

WITH G1 AS ( SELECT ${period_name} PERIOD_NAME, W_ORDER_DETAILS.ORDER_ID, W_ORDER_DETAILS.QUANTITY, W_ORDER_DETAILS.IS_FP FROM W_ORDER_DETAILS JOIN W_DATE ON W_ORDER_DETAILS.ORDER_DT_WID = W_DATE.ROW_WID
${joinInfo}
WHERE W_ORDER_DETAILS.VALID_STATUS = 1
${whereInfo}
), G2 AS ( SELECT G1.PERIOD_NAME, COUNT(DISTINCT G1.ORDER_ID) ALL_POCOUNT, SUM(G1.QUANTITY) ALL_QUANTITY, COUNT(DISTINCT CASE WHEN G1.IS_FP='Y' THEN ORDER_ID END) FP_POCOUNT, SUM(CASE WHEN G1.IS_FP='Y' THEN G1.QUANTITY END) FP_QUANTITY, COUNT(DISTINCT CASE WHEN G1.IS_FP='N' THEN ORDER_ID END) RP_POCOUNT, SUM(CASE WHEN G1.IS_FP='N' THEN G1.QUANTITY END) RP_QUANTITY FROM G1 ) SELECT (CASE WHEN ALL_POCOUNT=0 THEN 0 ELSE TRUNC(ALL_QUANTITY/ALL_POCOUNT,2) END) kpiVal, (CASE WHEN FP_POCOUNT=0 THEN 0 ELSE TRUNC(FP_QUANTITY/FP_POCOUNT,2) END) fpKpiVal, (CASE WHEN RP_POCOUNT=0 THEN 0 ELSE TRUNC(RP_QUANTITY/RP_POCOUNT,2) END) spKpiVal FROM G2

WITH G1 AS ( SELECT ${period_name} PERIOD_NAME, W_ORDER_DETAILS.ORDER_ID, W_ORDER_DETAILS.QUANTITY, W_ORDER_DETAILS.IS_FP FROM W_ORDER_DETAILS JOIN W_DATE ON W_ORDER_DETAILS.ORDER_DT_WID = W_DATE.ROW_WID
${joinInfo}
WHERE W_ORDER_DETAILS.VALID_STATUS = 1
${whereInfo}
), G2 AS ( SELECT G1.PERIOD_NAME, COUNT(DISTINCT G1.ORDER_ID) ALL_POCOUNT, SUM(G1.QUANTITY) ALL_QUANTITY, COUNT(DISTINCT CASE WHEN G1.IS_FP='Y' THEN ORDER_ID END) FP_POCOUNT, SUM(CASE WHEN G1.IS_FP='Y' THEN G1.QUANTITY END) FP_QUANTITY, COUNT(DISTINCT CASE WHEN G1.IS_FP='N' THEN ORDER_ID END) RP_POCOUNT, SUM(CASE WHEN G1.IS_FP='N' THEN G1.QUANTITY END) RP_QUANTITY FROM G1 GROUP BY G1.PERIOD_NAME ) SELECT PERIOD_NAME, (CASE WHEN ALL_POCOUNT=0 THEN 0 ELSE TRUNC(ALL_QUANTITY/ALL_POCOUNT,2) END) kpiVal, (CASE WHEN FP_POCOUNT=0 THEN 0 ELSE TRUNC(FP_QUANTITY/FP_POCOUNT,2) END) fpKpiVal, (CASE WHEN RP_POCOUNT=0 THEN 0 ELSE TRUNC(RP_QUANTITY/RP_POCOUNT,2) END) spKpiVal FROM G2 ORDER BY PERIOD_NAME ASC
