mapper.select:
  		select u.user_id userId,u.username,u.email,u.mobile,u.status,u.crate_time crateTime,
  		u.ssex,u.expire_date expireDate, u.description from t_user u
  		where 1=1

        	AND u.username = #{username}


	        AND u.ssex = #{ssex}


	        AND u.status = #{status}

  		order by u.user_id

mapper.update:
update t_user t set t.password=#{password},
					t.modify_time=sysdate,
					t.update_by=#{modifyName}
					t.firstLogin='Y' where t.user_id=#{userId}
